// List all your agents
const response = await fetch('https://dashboard.imash.io/api/v2/agents', {
  method: 'GET',
  headers: {
    'x-api-key': 'YOUR_SECRET_API_KEY',
    'Content-Type': 'application/json'
  }
});

const agents = await response.json();
console.log('Your agents:', agents);

// Create a new contact
const newContact = await fetch('https://dashboard.imash.io/api/v2/crm_contacts', {
  method: 'POST',
  headers: {
    'x-api-key': 'YOUR_SECRET_API_KEY',
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    name: 'John Doe',
    phone: '+15551234567',
    email: 'john@example.com'
  })
});

const contact = await newContact.json();
console.log('Created contact:', contact);
